const arrz = require("array_helperz")
const { getAllTagSnippetJoins, getAllProblemJoins, getAllCheatsheetJoins, getAllTaskJoins } = require("./oneToMany.js")
const { knex } = require("./knex")


async function importantProblems(knex, user_id) {
    let merged = await getAllProblemJoins(knex, user_id)
    let sorted = arrz.sortAsc(merged, "importance", "integer")
    return sorted
}
async function importantCheatsheets(knex, user_id) {
    let trimmed = await getAllCheatsheetJoins(knex, user_id)
    let sorted = arrz.sortAsc(trimmed, "importance", "integer")
    return sorted
}
async function importantTasks(knex, user_id) {
    let trimmed = await getAllTaskJoins(knex, user_id)
    let sorted = arrz.sortAsc(trimmed, "importance", "integer")
    return sorted
}
async function importantTagSnippets(knex, user_id) {
    let trimmed = await getAllTagSnippetJoins(knex, user_id)
    let sorted = arrz.sortAsc(trimmed, "importance", "integer")
    return sorted
}
async function testimportant() {
    let problems = await importantProblems(knex, 1)
    console.log(problems[0])
    console.log(problems[problems.length - 1])
    let tasks = await importantTasks(knex, 1)
    let tagsnippets = await importantTagSnippets(knex, 1)
    let cheatsheets = await importantCheatsheets(knex, 1)
}

module.exports = { importantProblems, importantCheatsheets, importantTasks, importantTagSnippets, testimportant };