const arrz = require("array_helperz")
const { getAllTagSnippetJoins, getAllProblemJoins, getAllCheatsheetJoins, getAllTaskJoins } = require("./oneToMany.js")
const { knex } = require("./knex")


async function timeSpentOnProblems(knex, user_id) {
    let merged = await getAllProblemJoins(knex, user_id)
    let sorted = arrz.sortDesc(merged, "seconds", "integer")
    return sorted
}
async function timeSpentOnCheatsheets(knex, user_id) {
    let trimmed = await getAllCheatsheetJoins(knex, user_id)
    let sorted = arrz.sortDesc(trimmed, "seconds", "integer")
    return sorted
}
async function timeSpentOnTasks(knex, user_id) {
    let trimmed = await getAllTaskJoins(knex, user_id)
    let sorted = arrz.sortDesc(trimmed, "seconds", "integer")
    return sorted
}
async function timeSpentOnTagSnippets(knex, user_id) {
    let trimmed = await getAllTagSnippetJoins(knex, user_id)
    let sorted = arrz.sortDesc(trimmed, "seconds", "integer")
    return sorted
}
async function testTimeSpent() {
    let problems = await timeSpentOnProblems(knex, 1)
    console.log(problems[0])
    console.log(problems[problems.length - 1])
    let tasks = await timeSpentOnTasks(knex, 1)
    let tagsnippets = await timeSpentOnTagSnippets(knex, 1)
    let cheatsheets = await timeSpentOnCheatsheets(knex, 1)
}

module.exports = { timeSpentOnProblems, timeSpentOnCheatsheets, timeSpentOnTasks, timeSpentOnTagSnippets, testTimeSpent };