const routes = require("./dist/index.js")

const app = require("express")()
const bodyParser = require("body-parser")

// app.use(bodyParser());
app.use(bodyParser.json())
app.use(bodyParser.urlencoded({ extended: false }))

app.use("/", routes.pageRouter)
app.use("/", routes.authRouter)
app.use("/", routes.postRouter)
app.use("/", routes.getRouter)
app.use("/", routes.sortsRouter)
app.use("/", routes.updateRouter)
app.use("/", routes.deleteRouter)
app.use("/", routes.searchRouter)

app.listen(2222, () => {
    console.log("app listening on port 2222")
})