// put syntax here 
function problem_link(options) {
    return '<a href="/problem/' + this.id + '">' + options.fn(this) + '</a>';
}

function list_wrapper(options) {
    let string = ` <div class="list has-overflow-ellipsis has-visible-pointer-controls has-hoverable-list-items">`
    return string + options.fn(this) + '</div>'
}

function isotope_sort_item(options) {
    let string = ` <span
  data-hash="${this.owl_hash}"
  data-difficulty="${this.difficulty}"
  data-start="${this.start}"
  data-status="${this.status}"
  data-end="${this.end}"
  data-created="${this.created}"
  data-seconds="${this.seconds}"
  data-importance="${this.importance}"
  data-usefulness="${this.usefulness}"
>`
    return string + options.fn(this) + '</span>'
}



module.exports = { problem_link, isotope_sort_item, list_wrapper };